import React , { useState }  from "react";
import Sidebar from "../dashboard/Sidebar";
import Chat from "./Chats";
import Update from "./Update";
import ChildCard from "../components/ChildCard";
import ActiveLogs from "./ActiveLog";
import {
  ContainerOutlined,
  DesktopOutlined,
  MailOutlined,
  MenuFoldOutlined,
  MenuUnfoldOutlined,
} from "@ant-design/icons";
import { Button, Menu } from "antd";

let items = [
  { label: "Update Issue", key: "1", icon: <ContainerOutlined /> },
  { label: "Chat", key: "2", icon: <ContainerOutlined /> },
  { label: "Active Logs", key: "3", icon: <ContainerOutlined /> },
  { label: "Children Ticket", key: "4", icon: <ContainerOutlined /> },
];
const PersonalTicket = () => {
  const [collapsed, setCollapsed] = useState(false);
  const [itemName, setItemName] = useState("")
  const toggleCollapsed = (e) => {
    setCollapsed(!collapsed);
  };

  const handleClick = (e)=>{
    console.log(e.key,'onSelect')
    setItemName(e.key)
  }

  return (
    <div>
      <body>
        <div className="col-md-2">
          <div className=" rightSidebar">
            <Button
              type="primary"
              onClick={toggleCollapsed}
              className="navButton"
            >
              {collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
            </Button>
            <Menu
              defaultSelectedKeys={["1"]}
              defaultOpenKeys={["1"]}
              onClick={handleClick}
              mode="inline"
              theme="light"
              inlineCollapsed={collapsed}
              items={items}
              className="menuStyle"
            />
          </div>
        </div>
        <div className="col-md-10">
          {  
            itemName && itemName == 1 ? <Update></Update> : itemName && itemName == 3? <ActiveLogs></ActiveLogs>: itemName && itemName == 4 ? <ChildCard></ChildCard>: <Chat></Chat> 
          }
          
        </div>
      </body>
    </div>
  );
};

export default PersonalTicket;
