import React, { useEffect, useState } from "react";
import { CloseCircleOutlined} from "@ant-design/icons";
import { Card } from "antd";
import { Divider,Popover,Avatar, Button,Progress, Rate} from "antd";
import { Link } from "react-router-dom";
import axios from 'axios';




const Content = () => {
  const [tickets, setTicket] = useState([]);
  const contents = (
    <div>
      <p>Content</p>
      <p>Content</p>
    </div>
  );
  useEffect(() => {
    const getTicket = async () => {
      const response = await axios.get("http://localhost:8000/ticket");
      if (response.data.tickets.length > 0) {
        let res = response.data.tickets;
        setTicket(res);
      }
    };
    getTicket();
  }, []);
  return (
    <div className="maincontent">
      <table className="table">
      {tickets.map((val) => {
       return ( <Card bordered={true} className="cardStyles">
          <tbody>
            <tr>
              <div className="row">
                <b className="tickeName">Q1-09-ANWE</b>
              </div>
              <div className="row">
                {/* <td ><div className="card1"></div></td> */}
                <td><div className="card2"><Link to={{pathname:`/ticket/${val.uuidIdentifier}`}}>{val.title}</Link></div></td>
                <td ><div className="card3"> <Avatar size={38} src={val.avatar} /> </div></td>
                <td>
                      <Popover content={contents} className="card4" title="Title" trigger="hover">
                        <Button>{val.ticketStatus}</Button>
                      </Popover>
                </td>
                <td><p className="card5"><Rate allowHalf defaultValue={val.priority} /></p></td>
                <td ><div className="card6"><Progress percent={80} size="small" status="active" /></div></td>
                <td ><div className="card7 rounded-circle"> <CloseCircleOutlined /></div></td>
              </div>
            </tr>
          </tbody>
        </Card>
       );
      })}
      </table>
    </div>
  );
};

export default Content;
