import React, { useState } from "react";

import {
  ContainerOutlined,
  DesktopOutlined,
  MailOutlined,
  MenuFoldOutlined,
  MenuUnfoldOutlined,
} from "@ant-design/icons";
import { Button, Menu, Modal, Space, Form, Input , Select} from "antd";
import CreateIssue from "./CreateIssue";
const Sidebar = () => {
  const [collapsed, setCollapsed] = useState(false);
  const [itemName, setItemName] = useState("");
  const toggleCollapsed = (e) => {
    setCollapsed(!collapsed);
  };
  const [open, setOpen] = useState(false);
  const [opens, showOpenModal] = useState(false);
  const [confirmLoading, setConfirmLoading] = useState(false);
  const [modalText, setModalText] = useState("Content of the modal");
  const showModal = () => {
    alert("hi");
    setOpen(true);
  };
  const handleOk = () => {
    setModalText("The modal will be closed after two seconds");
    setConfirmLoading(true);
    setTimeout(() => {
      setOpen(false);
      setConfirmLoading(false);
    }, 2000);
  };
  const handleCancel = () => {
    console.log("Clicked cancel button");
    setOpen(false);
  };

  //for sprint
  const [isSprintModalOpen, setIsSprintModalOpen] = useState(false);
  const showSprintModal = () => {
    setIsSprintModalOpen(true);
  };
  const handleSprintOk = () => {
    setIsSprintModalOpen(false);
  };
  const handleSprintCancel = () => {
    setIsSprintModalOpen(false);
  };
  const items = [
    { label: "Analytics", key: "1", icon: <ContainerOutlined /> },
    {
      label: "Create Project",
      key: "2",
      icon: <Button type="primary" onClick={showModal}></Button>,
    },
    {
      label: "WORKSPACE_1",
      key: "sub1",
      icon: <MailOutlined />,
      children: [
        {
          label: "Create Issue",
          key: "6",
          icon: <Button type="primary" onClick={showOpenModal}></Button>,
        },
        {
          label: "Add Sprint",
          key: "7",
          icon: <Button type="primary" onClick={showSprintModal}></Button>,
        },
        {
          label: "Add Ticket",
          key: "7",
          icon: <Button type="primary" onClick={showSprintModal}></Button>,
        },
      ],
    },
  ];
  return (
    <>
      {/* issue modal */}
      <Modal
        title="Modal 1000px width"
        centered
        open={opens}
        onOk={() => showOpenModal(false)}
        onCancel={() => showOpenModal(false)}
        width={1000}
        height={1000}
      >
        {/* add the issue page */}
        <CreateIssue></CreateIssue>
      </Modal>
      {/* project modal */}
      <Modal
        title="Add Project"
        open={open}
        onOk={handleOk}
        confirmLoading={confirmLoading}
        onCancel={handleCancel}
      >
        <Form
          labelCol={{ span: 4 }}
          wrapperCol={{ span: 14 }}
          layout="horizontal"
          style={{ maxWidth: 600 }}
        >
          <Form.Item
            label="name"
            name="name"
            rules={[
              {
                required: true,
                message: "Please input your project name!",
              },
            ]}
          >
            <Input />
          </Form.Item>
        </Form>
      </Modal>
      {/* sprint Modal */}
      <Modal
        title="Basic Modal"
        open={isSprintModalOpen}
        onOk={handleSprintOk}
        onCancel={handleSprintCancel}
      >
        <Form
          labelCol={{ span: 4 }}
          wrapperCol={{ span: 14 }}
          layout="horizontal"
          style={{ maxWidth: 600 }}
        >
          <Form.Item
            label="sprint"
            name="sprint"
            rules={[
              {
                required: true,
                message: "Please input your sprint name!",
              },
            ]}
          >
            <Input />
          </Form.Item>
        </Form>
      </Modal>
      {/* ticket modal */}
      <Modal
        title="Basic Modal"
        open={isSprintModalOpen}
        onOk={handleSprintOk}
        onCancel={handleSprintCancel}
      >
        <Form
          labelCol={{ span: 4 }}
          wrapperCol={{ span: 14 }}
          layout="horizontal"
          style={{ maxWidth: 600 }}
        >
          <Form.Item label="Sprint">
            <Select>
              <Select.Option value="demo">#SPRINT1</Select.Option>
              <Select.Option value="demo">#SPRINT1</Select.Option>
              <Select.Option value="demo">#SPRINT1</Select.Option>
              <Select.Option value="demo">#SPRINT1</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item
            label="ticket"
            name="ticket"
            rules={[
              {
                required: true,
                message: "Please input your ticket name!",
              },
            ]}
          >
            <Input />
          </Form.Item>
        </Form>
      </Modal>
      <div className=" rightSidebar">
        <Button type="primary" onClick={toggleCollapsed} className="navButton">
          {collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
        </Button>
        <Menu
          defaultSelectedKeys={["1"]}
          defaultOpenKeys={["sub1"]}
          mode="inline"
          theme="light"
          inlineCollapsed={collapsed}
          items={items}
          className="menuStyle"
        />
      </div>
    </>
  );
};

export default Sidebar;
